mapper.select:
SELECT months.month AS `month`, (IFNULL(o.total_sales, 0) - IFNULL(p.total_purchase_amount, 0)) AS total_profit FROM ( SELECT DATE_FORMAT(created_at, '%Y-%m') AS month FROM `order` WHERE
YEAR(created_at) = #{targetYear}
YEAR(created_at) = YEAR(NOW())
UNION SELECT DATE_FORMAT(created_at, '%Y-%m') AS month FROM purchase_record WHERE
YEAR(created_at) = #{targetYear}
YEAR(created_at) = YEAR(NOW())
) months LEFT JOIN ( SELECT DATE_FORMAT(created_at, '%Y-%m') AS order_month, SUM(total_amount) AS total_sales FROM `order` WHERE status = 3 AND
YEAR(created_at) = #{targetYear}
YEAR(created_at) = YEAR(NOW())
GROUP BY order_month ) o ON months.month = o.order_month LEFT JOIN ( SELECT DATE_FORMAT(created_at, '%Y-%m') AS purchase_month, SUM(total_amount) AS total_purchase_amount FROM purchase_record WHERE
YEAR(created_at) = #{targetYear}
YEAR(created_at) = YEAR(NOW())
GROUP BY purchase_month ) p ON months.month = p.purchase_month ORDER BY months.month ASC;
